import React from "react";
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
let value ;
let email 
let pwd



async function api(event, value) {
  let email = document.querySelector(".email").value;
  let pwd = document.querySelector(".pwd").value;
  let confirmPwd = document.querySelector(".confirmPwd").value;
  console.log("email", pwd, confirmPwd, email);
  event.preventDefault();

  // if(!email || pwd || confirmPwd)
  // {
  //   notify();
  // }

  const res = await axios({
    method: "POST",
    url: "/api",
    data: {
      email: email,
      password: pwd,
      confirmPassword: confirmPwd,
    },
  });
  console.log("res", res);
}

const notify = (value) => 
{
  toast.error(value, {
    position: "top-right",
    autoClose: 5000,
    hideProgressBar: true,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
    theme: "colored",
    });
};

let handleOnChange = ( email ) => {

  // don't remember from where i copied this code, but this works.
  let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  if ( re.test(email) ) {
      // this is a valid email address
      // call setState({email: email}) to update the email
      // or update the data in redux store.
      value = "its email----";
      notify(value);
  }
  else {
      // invalid email, maybe show an error to the user.
      value = "not-email----";
      notify(value);
  }

}

function App() {
  // const [data, setData] = React.useState(null);

  // React.useEffect(() => {
  //   fetch("/api")
  //     .then((res) => res.json())
  //     .then((data) => setData(data.message));
  // }, []);
  return (
    <form action="" className="signup-form">
      <div className="form-header">
        <h1>Create Account in Zee5</h1>
      </div>
      <div className="form-body">
        <div className="row">
          <div className="input-group">
            <label for="">Email/Phone Number</label>
            <input className="email" type="text" onChange={ handleOnChange() } />
          </div>
        </div>
        <div className="row">
          <div className="input-group">
            <label for="">Password</label>
            <input className="pwd" type="password" />
          </div>

          <div className="input-group">
            <label for="">Confirm Password</label>
            <input className="confirmPwd" type="password" />
          </div>
        </div>
      </div>
      <div className="form-footer">
        {/* <button className="btn" onClick={api} >
          Create Account
        </button> */}
        <button className="btn" onClick={(event) => {
                  api(event);
                  notify();
                }} >
          Create Account
        </button>

        <ToastContainer
           position="top-right"
           autoClose={5000}
           hideProgressBar={true}
           newestOnTop={false}
           closeOnClick
           rtl={false}
           pauseOnFocusLoss
           draggable
           pauseOnHover
           theme="colored"/>
<ToastContainer />
      </div>
    </form>
  );
}

export default App;
